script:
frappe.ui.form.on('Customer Visit', {

    // ----------------------
    // Form Load
    // ----------------------
    onload: function(frm) {
        // Fetch states from Address DocType (client-side)
        frappe.db.get_doc('DocType', 'Address').then(address_doctype => {
            let state_field = address_doctype.fields.find(f => f.fieldname === 'state');
            if (state_field && state_field.options) {
                let states = state_field.options.split('\n');
                frm.set_df_property('state', 'options', states.join('\n'));
            }
        });
    },

    // ----------------------
    // Form Refresh
    // ----------------------
    refresh: function(frm) {
        toggle_buttons(frm);
        calculate_days_before(frm);
    },

    // ----------------------
    // Field Change Events
    // ----------------------
    visit_date: function(frm) {
        calculate_days_before(frm);
    },

    d_day: function(frm) {
        calculate_days_before(frm);
    },

    customer_type: function(frm) {
        toggle_buttons(frm);
    },

    customer: async function(frm) {
        if (!frm.doc.customer) {
            frm.set_value('customer_details', '');
            frm.fields_dict.address_html.$wrapper.html('');
            return;
        }

        // Fetch customer doc including primary address
        let customer_doc = await frappe.db.get_doc('Customer', frm.doc.customer);

        let address_html = '';
        let details_html = '';

        if (customer_doc.customer_primary_address) {
            // Fetch the primary address doc
            let addr = await frappe.db.get_doc('Address', customer_doc.customer_primary_address);

            let full_address = `
                ${addr.address_line1 || ''}<br>
                ${addr.address_line2 || ''}<br>
                ${addr.city || ''}, ${addr.state || ''} - ${addr.pincode || ''}<br>
            `;

            address_html = `
                <div style="line-height:1.5; font-size:13px;">
                    <b>Address:</b><br>${full_address}
                    <b>Contact:</b> ${addr.phone || ''}<br>
                    <b>GSTIN:</b> ${addr.gstin || ''}<br>
                </div>
            `;

            details_html = `
                <b>Name:</b> ${customer_doc.customer_name}<br>
                <b>Address:</b> ${addr.address_line1 || ''}, ${addr.city || ''}<br>
                <b>Contact:</b> ${addr.phone || ''}<br>
                <b>GSTIN:</b> ${addr.gstin || ''}<br>
            `;
        } else {
            details_html = `<b>Name:</b> ${customer_doc.customer_name}<br><span style="color:gray;">No primary address found</span>`;
            address_html = `<span style="color:gray;">No primary address linked</span>`;
        }

        frm.set_value('customer_details', details_html);
        frm.refresh_field('customer_details');
        frm.fields_dict.address_html.$wrapper.html(address_html);
    },

    edit_customer_button: function(frm) {
        if (frm.doc.customer) {
            frappe.set_route('Form', 'Customer', frm.doc.customer);
        } else {
            frappe.msgprint('Please select a customer first');
        }
    },

    create_new_customer_button: function(frm) {
        frappe.new_doc('Customer');
    }

});

// ----------------------
// Helper Functions
// ----------------------
function calculate_days_before(frm) {
    if(frm.doc.visit_date && frm.doc.d_day) {
        frm.set_value('days_before_d_day', frappe.datetime.get_diff(frm.doc.d_day, frm.doc.visit_date));
    } else {
        frm.set_value('days_before_d_day', null);
    }
}

function toggle_buttons(frm) {
    if (frm.doc.customer_type === 'Old Customer') {
        frm.set_df_property('customer', 'reqd', 1);
        frm.set_df_property('edit_customer_button', 'hidden', 0);
        frm.set_df_property('create_new_customer_button', 'hidden', 1);
    } else if (frm.doc.customer_type === 'New Customer') {
        frm.set_df_property('customer', 'reqd', 0);
        frm.set_df_property('edit_customer_button', 'hidden', 1);
        frm.set_df_property('create_new_customer_button', 'hidden', 0);
        frm.set_value('customer', '');
        frm.set_value('customer_details', '');
        frm.fields_dict.address_html.$wrapper.html('');
    }
}


// ----------------------
// Filter Customer by State/City (Working Version)
// ----------------------
function set_customer_query(frm) {
    frm.set_query('customer', async function() {
        let filters = [];
        if (frm.doc.state) filters.push(['state', '=', frm.doc.state]);
        if (frm.doc.city) filters.push(['city', '=', frm.doc.city]);

        if (filters.length === 0) return {}; // no filter if both empty

        // Fetch addresses first
        let addresses = await frappe.db.get_list('Address', {
            fields: ['name'],
            filters: filters
        });

        let address_names = addresses.map(a => a.name);

        return {
            filters: {
                customer_primary_address: ['in', address_names]
            }
        };
    });
}

frappe.ui.form.on('Stock Entry', {
    refresh: function(frm) {
        frm.doc.items.forEach(row => {
            let workstation = frm.doc.custom_factory__location || "";
            let bill_no = frm.doc.custom_bill_no || "";
            let combined_value = workstation && bill_no
                ? (workstation + "-" + bill_no)
                : (workstation || bill_no);

            frappe.model.set_value(row.doctype, row.name, 'custom_factory_bill_no', combined_value);
        });
    }
});

frappe.ui.form.on("Sales Invoice", {
    refresh(frm) {
        // Only show button for submitted invoices with a key
        if (frm.doc.docstatus === 1 && frm.doc.custom_public_access_key) {
            frm.add_custom_button(__("Send via WhatsApp"), async function() {
                try {
                    // ‚úÖ Fetch Customer Phone Number
                    let mobile = "";
                    
                    // Try to get phone from customer address
                    if (frm.doc.customer_address) {
                        const address_resp = await frappe.call({
                            method: "frappe.client.get",
                            args: {
                                doctype: "Address",
                                name: frm.doc.customer_address
                            }
                        });

                        if (address_resp && address_resp.message) {
                            let addr = address_resp.message;
                            mobile = addr.phone || addr.mobile_no || "";
                        }
                    }

                    // If not found in address, try customer mobile
                    if (!mobile && frm.doc.contact_mobile) {
                        mobile = frm.doc.contact_mobile;
                    }

                    if (!mobile) {
                        frappe.msgprint({
                            title: __("Phone Number Missing"),
                            message: __("Customer phone number not found. Please add it in Customer Address."),
                            indicator: "red"
                        });
                        return;
                    }

                    // ‚úÖ Clean and add country code
                    mobile = mobile.replace(/\D/g, ""); // Remove non-digits
                    if (!mobile.startsWith("91")) {
                        mobile = "91" + mobile;
                    }

                    // ‚úÖ Build secure link
                    const secure_link = `${window.location.origin}/api/method/shreerakhi_customizations.api.public.get_invoice?key=${frm.doc.custom_public_access_key}`;

                    // ‚úÖ Compose WhatsApp message
                    const amount = frm.doc.rounded_total || frm.doc.grand_total;
                    const msg = `‡§®‡§Æ‡§∏‡•ç‡§§‡•á ${frm.doc.customer_name},\n\n‡§Ü‡§™‡§ï‡§æ Invoice *${frm.doc.name}* ‡§§‡•à‡§Ø‡§æ‡§∞ ‡§π‡•à‡•§\nüí∞ ‡§∞‡§æ‡§∂‡§ø: ‚Çπ${amount}\n\nüìé Invoice ‡§¶‡•á‡§ñ‡•á‡§Ç/Download ‡§ï‡§∞‡•á‡§Ç:\n${secure_link}\n\nüôè ‡§ß‡§®‡•ç‡§Ø‡§µ‡§æ‡§¶!`;

                    // ‚úÖ Open WhatsApp
                    const whatsapp_url = `https://wa.me/${mobile}?text=${encodeURIComponent(msg)}`;
                    window.open(whatsapp_url, "_blank");

                    frappe.show_alert({
                        message: __("WhatsApp opened successfully!"),
                        indicator: "green"
                    }, 3);

                } catch (error) {
                    console.error("WhatsApp Error:", error);
                    frappe.msgprint({
                        title: __("Error"),
                        message: __("Error while preparing WhatsApp message. Please check console."),
                        indicator: "red"
                    });
                }
            }, __("Actions"));
        }
    }
});

frappe.ui.form.on('Sales Order', {
    delivery_date: function(frm) {
        if(frm.doc.delivery_date) {
            frm.doc.items.forEach(function(item) {
                item.delivery_date = frm.doc.delivery_date;
            });
            frm.refresh_field('items');
        }
    }
});
