script:
frappe.ui.form.on('Stock Entry', {
    refresh: function(frm) {
        frm.doc.items.forEach(row => {
            let workstation = frm.doc.custom_factory__location || "";
            let bill_no = frm.doc.custom_bill_no || "";
            let combined_value = workstation && bill_no
                ? (workstation + "-" + bill_no)
                : (workstation || bill_no);

            frappe.model.set_value(row.doctype, row.name, 'custom_factory_bill_no', combined_value);
        });
    }
});

frappe.ui.form.on("Sales Invoice", {
    refresh: function(frm) {
        frm.fields_dict['custom_bos_transporter'].get_query = function(doc) {
            return {
                filters: {
                    is_transporter: 1
                }
            };
        };
    }
});
