css:
/*css for pdf*/
@media print{
    .payment_qr img {
        width:81px !important;
        height: 81px;
    }
}

.payment_qr img {
    width:81px !important;
    height: 81px;
}
.payment_qr{
    float:left;
}


/*general css*/  
b{
    color : initial;
}

p{
    font-size : 12px !important;
    word-break:break-all;
    margin:0px !important;
}

table{
    width:100%;
}


/*section after heading of print format*/
div[data-label="First Section"] > div:nth-child(1){
    width:40% !important;
    padding-right:0px !important;
    padding-top:20px !important;
}
div[data-label="First Section"] > div:nth-child(2) {
    width:34% !important;
    padding-right:0px !important;
    padding-left:6px !important;
    padding-top:20px !important;
}
div[data-label="First Section"] > div:nth-child(3) {
    width:26% !important;
    padding-left:0px !important;
}


/*css for place of supply*/
div[data-fieldname="place_of_supply"]{
    padding-top:5px !important;
    padding-bottom:5px !important;
    margin-top:0px !important;
    margin-bottom:0px !important;
}
div[data-fieldname="place_of_supply"] > div > label{
    margin-bottom:0px !important;
}
div[data-fieldname="place_of_supply"] > div:nth-child(1){
    width:50% !important;
    line-height: 1;
}
div[data-fieldname="place_of_supply"] > div:nth-child(2){
    width:50% !important;
    line-height: 1;
}


/*css for posting date*/
div[data-fieldname="posting_date"]{
    margin-top:0px;
    margin-bottom:0px;
}
div[data-fieldname="posting_date"] > div:nth-child(1){
    width:50% !important;
    line-height:1.6;
}
div[data-fieldname="posting_date"] > div:nth-child(2){
    width:50% !important;
    line-height:1.6;
}


/*css for first section after heading second column*/
.first_section_second_column{
    padding-bottom:5px !important;
    padding-top:5px !important;
}


/*css for first section after heading third column - qr code column*/
.paid_unpaid{
    padding-bottom:5px !important;
    text-align:end;
}
.paid_unpaid > p{
    font-size:120% !important;
    line-height:1;
}


/*css for irn*/
.print-format p.field-property{
    padding-top:5px !important;
    padding-bottom:5px !important;
}

/*css for company-details*/
table.company-details {
    width:66%;
    display: inline-block;
}

table.company-details tbody {
    display: table;
    width:100%;
}

table.company-details:nth-child(2) {
    width: 32%;
}

/*css for due_date adn paid amount*/
div[data-fieldname="due_date"] > div{
    width:50%;
}
div[data-fieldname="due_date"] > div > label {
    margin:0px ;
}
div[data-fieldname="due_date"] > div:nth-child(2){
    width:50%;
    text-align:end;
}
div[data-fieldname="due_date"]{
    margin-top:0px;
    margin-bottom:0px;
}
div[data-fieldname="paid_amount"]{
    margin-top:0px;
    margin-bottom:0px;
    padding-top:5px;
    padding-bottom:5px;
    line-height:1;
}
div[data-fieldname="paid_amount"] > div > label {
    margin:0px;
}

.data-field{
    margin-top:0px;
    margin-bottom:0px;
}


/*changing css of erpnext elements*/
#taxes-section > .row > .col-xs-6:first-child {
    display: none;
}

#taxes-section > .row > .col-xs-6 {
    width: 100%;
}

#taxes-section > .row > .col-xs-6 > .row {
    padding-top:5px !important;
    padding-bottom:5px !important;
    line-height:1;
}

#taxes-section > .row > .col-xs-6 > .row > .col-xs-5 > label {
    margin:0px !important;
}

div[data-label="erpnext"] > div:nth-child(2) > .row > .col-xs-5 > label {
    margin:0px !important;
}

.column-break > .row:first-child {
    line-height:1;
    padding-bottom:5px !important;
}

div[data-fieldname="grand_total"] {
    margin-top:0px !important;
    margin-bottom:0px !important;
}
div[data-fieldname="rounded_total"] {
    margin-top:0px !important;
    margin-bottom:0px !important;
}
div[data-fieldname="in_words"] {
    margin-top:0px ;
    margin-bottom:0px;
    line-height:1;
    padding-top:5px;
}


/* last section of print format*/

