html:
{% if doc.item_code %}
    <img src="{{ custom_image_link }}" 
         style="max-width:80px; max-height:80px;">
{% endif %}

{% set child_table_data = doc.items %}

{% set result_list = [] %}

{% for row in child_table_data %}
    {% set row_dict = {
        'item_name': row.item_name,
        'barcode' : row.barcode,
        'description': row.description,
        'gst_hsn_code': row.gst_hsn_code,
        'qty': row.qty,
        'uom': row.uom,
        'curr': doc.price_list_currency,
        'rate': row.rate,
        'item_tax_rate': row.item_tax_template,
        'taxable_value': row.taxable_value,
        'amount': row.amount
    } %}
    {% set _ = result_list.append(row_dict) %}
{% endfor %}



{% set sorted_result_list = result_list | sort(attribute='item_code') %}

{% for row in sorted_result_list %}
    {% set _ = row.update({'idx': loop.index}) %}
{% endfor %}

